{"root":{"children":[{"type":"header","version":2,"size":"small","style":"dark","buttonEnabled":false,"buttonUrl":"","buttonText":"","header":"<span style="white-space: pre-wrap;">DevOps &amp; Deploy</span>","subheader":null,"backgroundImageSrc":"__GHOST_URL__/content/images/2025/01/1491.jpg","accentColor":"#000000","alignment":"center","backgroundColor":"#000000","backgroundImageWidth":1500,"backgroundImageHeight":823,"backgroundSize":"cover","textColor":"#FFFFFF","buttonColor":"#ffffff","buttonTextColor":"#000000","layout":"full","swapped":false},{"type":"html","version":1,"html":"<div id="rss-feed" style="padding: 20px;"></div>
<div id="load-more-container" style="text-align: center; margin-top: 20px;">
  <button id="load-more-button" style="background-color: #007bff; color: white; border: none; border-radius: 5px; padding: 10px; cursor: pointer;">
    Load More
  </button>
</div>
<script>
  const rssFeedUrl = 'https://www.mhto.in/tag/devops-and-deploy/rss';
  let totalPostsLoaded = 0;
  const postsPerPage = 4; // Show four posts initially

  async function fetchRSS() {
    try {
      const response = await fetch(rssFeedUrl);
      const text = await response.text();
      const parser = new DOMParser();
      const xml = parser.parseFromString(text, "text/xml");
      const items = xml.querySelectorAll("item");

      if (items.length === 0) {
        document.getElementById('load-more-container').style.display = 'none'; // Hide button if no items
        return;
      }

      let html = '';
      for (let i = totalPostsLoaded; i < Math.min(totalPostsLoaded + postsPerPage, items.length); i++) {
        const item = items[i];
        const title = item.querySelector("title").textContent;
        const link = item.querySelector("link").textContent;
        const description = item.querySelector("description").textContent;
        const pubDate = new Date(item.querySelector("pubDate").textContent); // Get publication date

        // Extract image from media:content
        const mediaContent = item.getElementsByTagName("media:content")[0];
        const imageUrl = mediaContent ? mediaContent.getAttribute("url") : '';

        // Limit description to 25 words
        const limitedDescription = description.split(' ').slice(0, 25).join(' ') + (description.split(' ').length > 25 ? '...' : '');

        // Calculate time ago
        const timeAgo = getTimeAgo(pubDate);

        // Create a card for each post
        html += `
          <div style="border: 1px solid #ddd; border-radius: 8px; margin-bottom: 20px; box-shadow: 0 2px 5px rgba(0,0,0,0.1); overflow: hidden;">
            <a href="${link}" target="_blank" style="text-decoration: none; color: inherit;">
              ${imageUrl ? `<img src="${imageUrl}" alt="${title}" style="width: 100%; height: auto;"/>` : '<img src="https://via.placeholder.com/300x200" alt="Placeholder Image" style="width:100%; height:auto;"/>'}
              <div style="padding: 15px;">
                <h3 style="margin: 0;">${title}</h3>
                <p style="margin-top: 10px; color: #555;">${limitedDescription}</p>
                <div style="display:flex; justify-content: space-between; align-items:center;">
                  <span style="color:#999;">${timeAgo} ago</span>
                  <button style="background-color: black; color: white; border: none; border-radius: 15px; padding: 5px 10px; cursor: pointer;">
                    <span style="color: white;">Read More</span>
                  </button>
                </div>
              </div>
            </a>
          </div>`;
      }

      document.getElementById('rss-feed').innerHTML += html;
      totalPostsLoaded += postsPerPage;

      // Hide the Load More button if all posts have been loaded
      if (totalPostsLoaded >= items.length) {
        document.getElementById('load-more-container').style.display = 'none';
      }
    } catch (error) {
      console.error('Error fetching RSS feed:', error);
    }
  }

  function getTimeAgo(date) {
    const now = new Date();
    const secondsAgo = Math.floor((now - date) / 1000);
    let interval = Math.floor(secondsAgo / 31536000); // Years
    if (interval > 1) return `${interval} years`;
    interval = Math.floor(secondsAgo / 2592000); // Months
    if (interval > 1) return `${interval} months`;
    interval = Math.floor(secondsAgo / 86400); // Days
    if (interval > 1) return `${interval} days`;
    interval = Math.floor(secondsAgo / 3600); // Hours
    if (interval > 1) return `${interval} hours`;
    interval = Math.floor(secondsAgo / 60); // Minutes
    if (interval > 1) return `${interval} minutes`;
    return `${secondsAgo} seconds`;
  }

  document.getElementById('load-more-button').addEventListener('click', fetchRSS);

  // Initial fetch to load the first batch of posts
  fetchRSS();
</script>
