{"root":{"children":[{"children":[],"direction":null,"format":"","indent":0,"type":"extended-heading","version":1,"tag":"h2"},{"children":[{"detail":0,"format":0,"mode":"normal","style":"","text":"JavaScript is a versatile programming language primarily used for enhancing web pages with interactivity and dynamic content. It is an essential part of web development alongside HTML and CSS.","type":"extended-text","version":1}],"direction":"ltr","format":"","indent":0,"type":"paragraph","version":1},{"type":"html","version":1,"html":"<script>
document.addEventListener('DOMContentLoaded', function() {
    // Create and append CSS styles for accordion
    const style = document.createElement('style');
    style.textContent = `
        /* Accordion Styles */
        .accordion {
            display: flex;
            flex-direction: column;
            gap: 10px;
            width: 100%;
        }

        .accordion__item {
            border: 1px solid grey;
            border-radius: 10px;
            overflow: hidden;
        }

        .accordion__header {
            padding: 20px 25px;
            font-weight: 600;
            cursor: pointer;
            position: relative;
        }

        .accordion__header::after {
            content: '';
            background: url(https://www.svgrepo.com/show/357035/angle-down.svg) no-repeat center;
            width: 20px;
            height: 20px;
            transition: .4s;
            display: inline-block;
            position: absolute;
            right: 20px;
            top: 20px;
            z-index: 1;
        }

        .accordion__header.active {
            background: #e5f3fa;
        }

        .accordion__header.active::after {
            transform: rotateX(180deg);
        }

        .accordion__item .accordion__content {
            padding: 0 25px;
            max-height: 0;
            transition: .5s;
            overflow: hidden;
        }
    `;
    document.head.appendChild(style);

    function createHeadingDropdowns() {
        const postContent = document.querySelector('.gh-content');
        if (!postContent) return;

        const headings = postContent.querySelectorAll('h2, h3, h4');

        headings.forEach((heading, index) => {
            const nextHeading = headings[index + 1];
            const contentElements = getContentBetweenHeadings(heading, nextHeading);

            if (contentElements.length > 0) {
                const dropdownWrapper = createDropdownWrapper(heading);
                wrapContent(contentElements, dropdownWrapper);

                // Insert the wrapper after the heading
                heading.parentNode.insertBefore(dropdownWrapper.wrapper, heading.nextSibling);

                // Remove the original heading from the DOM
                heading.remove();

                // Open the first accordion item by default
                if (index === 0) {
                    dropdownWrapper.header.classList.add('active');
                    dropdownWrapper.contentDiv.style.maxHeight = dropdownWrapper.contentDiv.scrollHeight + "px"; // Expand
                }
            }
        });

        function getContentBetweenHeadings(startHeading, endHeading) {
            const content = [];
            let currentElement = startHeading.nextElementSibling;

            while (currentElement && 
                   (!endHeading || currentElement !== endHeading)) {
                if (!['H2', 'H3', 'H4'].includes(currentElement.tagName)) {
                    content.push(currentElement);
                } else {
                    break; // Stop if we hit another heading
                }
                currentElement = currentElement.nextElementSibling;
            }

            return content;
        }

        function createDropdownWrapper(heading) {
            const wrapper = document.createElement('div');
            wrapper.classList.add('accordion__item');

            const header = document.createElement('div');
            header.classList.add('accordion__header');

            header.textContent = heading.textContent; // Copy the heading text

            const contentDiv = document.createElement('div');
            contentDiv.classList.add('accordion__content');

            wrapper.appendChild(header);

            header.addEventListener('click', () => {
                header.classList.toggle('active');
                if (header.classList.contains('active')) {
                    contentDiv.style.maxHeight = contentDiv.scrollHeight + "px"; // Expand
                } else {
                    contentDiv.style.maxHeight = null; // Collapse
                }
            });

            return { wrapper, contentDiv, header };
        }

        function wrapContent(elements, { wrapper, contentDiv }) {
            elements.forEach(el => {
                contentDiv.appendChild(el);
            });

            // Append the content div to the wrapper only once
            wrapper.appendChild(contentDiv);

            // Set initial max-height to zero to hide the content
            contentDiv.style.maxHeight = "0";
        }
    }

    createHeadingDropdowns();
});
</script>
